// GraphPage.js
import React, { useEffect, useState } from 'react';
import zoomPlugin from 'chartjs-plugin-zoom';
import { Scatter } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    Title,
    Tooltip,
    Legend,
    zoomPlugin
);

function GraphPage({ selectedArticles, setPage }) {
    const [articlePoints, setArticlePoints] = useState([]);

    const articleColors = ['#FF6384', '#36eb60', '#FFCE56', '#f3aafd', '#9966FF'];

    useEffect(() => {
        console.log(selectedArticles);
        if (selectedArticles.length) {
            fetchSelectedArticles();
        }
    }, [selectedArticles]);

    const fetchArticleData = async (title) => {
        try {
            const response = await fetch(`http://localhost:3001/api/articles/${title}`);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            return response.json();
        } catch (error) {
            console.error(`Failed to fetch article: ${title}`, error);
            return null; // or use a fallback value
        }
    };

    const fetchSelectedArticles = async () => {
        try {
            console.log(selectedArticles)
            const requests = selectedArticles.map(title => fetchArticleData(title));
            const articlesResults = await Promise.allSettled(requests);
            const articlesData = articlesResults
                .filter(result => result.status === 'fulfilled' && result.value)
                .map(result => result.value);

            const points = articlesData.map(article => ({
                x: article.sentiment_score,
                y: article.subjectivity_score,
                label: `Title: ${article.title}\nPolarity: ${article.sentiment_score}\nSubjectivity: ${article.subjectivity_score}\nCategory: ${article.category}\nSource: ${article.source}`,
            }));

            setArticlePoints(points);
        } catch (error) {
            console.error('Error fetching selected articles', error);
            // Handle or propagate the error as needed
        }
    };

    const graphData = {
        datasets: [
            {
                label: 'Articles',
                data: articlePoints,
                backgroundColor: articlePoints.map((_, i) => articleColors[i % articleColors.length]),
                pointRadius: 5,
                hoverBackgroundColor: 'rgba(255, 255, 255, 0.8)',
                hoverBorderColor: 'rgba(255, 255, 255, 1)',
                hoverBorderWidth: 2,
                hoverRadius: 10,
            },
        ],
    };

    const graphOptions = {
        scales: {
            x: {
                type: 'linear',
                position: 'bottom',
                title: {
                    display: true,
                    text: 'Polarity (Negative vs Positive Sentiment)',
                    font: {
                        size: 24,
                        family: 'Montserrat',
                        weight: 600
                    },
                    color: '#ffffff'
                },
                min: -1,
                max: 1,
                ticks: {
                    color: '#ffffff',
                    stepSize: 0.25,
                },
                grid: {
                    color: '#ffffff',
                }
            },
            y: {
                title: {
                    display: true,
                    text: 'Subjectivity (Opinion vs Fact)',
                    font: {
                        size: 24,
                        family: 'Montserrat',
                        weight: 600
                    },
                    color: '#ffffff'
                },
                min: 0,
                max: 1,
                ticks: {
                    stepSize: 0.2,
                    color: '#e5e5e5'
                },
                grid: {
                    color: '#e5e5e5'
                }
            },
        },
        plugins: {
            title: {
                display: false,
                text: 'Article Subjectivity and Polarity',
            },
            legend: {
                display: false,
            },
            tooltip: {
                callbacks: {
                    label: function(context) {
                        var label = context.raw.label;
                        if (label) {
                            // If the label is a single string, split it by the newline character
                            // and return the array of strings to create multiline labels.
                            return label.split('\n');
                        }
                        return label || '';
                    }
                }
            },
            zoom: {
                pan: {
                    enabled: true,
                    mode: 'xy'
                },
                zoom: {
                    wheel: {
                        enabled: true,
                    },
                    pinch: {
                        enabled: true
                    },
                    mode: 'xy',
                }
            }
        },
        responsive: true,
        maintainAspectRatio: false,
    };

    return (
        <div className="card">
            <div>
                <h1>Article Subjectivity and Polarity</h1>
                <div style={{ height: '500px', width: '100%' }}>
                    <Scatter data={graphData} options={graphOptions} />
                </div>
                <button className="modern-button" onClick={() => setPage(1)}>Back to Article Selection</button>
            </div>
        </div>
    );
}

export default GraphPage;
