// App.js
import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { Line, Scatter } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

import { useState, useEffect } from "react";

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

// Scatter plot data and options
const scatterData = {
    datasets: [],
};

const scatterOptions = {
    scales: {
        x: {
            type: 'linear',
            position: 'bottom',
            title: {
                display: true,
                text: 'Polarity (Negative vs Positive Sentiment)',
            },
            min: -1,
            max: 1,
            ticks: {
                stepSize: 0.25,
            },
        },
        y: {
            title: {
                display: true,
                text: 'Subjectivity (Opinion vs Fact)',
            },
            min: 0,
            max: 1,
            ticks: {
                stepSize: 0.2,
            },
        },
    },
    plugins: {
        title: {
            display: true,
            text: 'Article Subjectivity and Polarity',
        },
        legend: {
            display: false,
        },
        tooltip: {
            callbacks: {
                label: function(context) {
                    return context.dataset.label || '';
                }
            }
        }
    },
    responsive: true,
    maintainAspectRatio: false,
};

function App() {
    const [articles, setArticles] = useState([]);
    const [currentArticle, setCurrentArticle] = useState(null);

    // Fetch articles
    const fetchArticles = () => {
        fetch("/api/articles")
            .then((res) => res.json())
            .then((data) => setArticles(data.articles))
            .catch((error) => console.error('Error fetching articles:', error));
    };

    // Function to fetch a single article
    const fetchArticle = (id) => {
        setCurrentArticle(null); // Clear the current article
        fetch(`/api/articles/${id}`)
            .then((res) => res.json())
            .then((data) => setCurrentArticle(data))
            .catch((error) => console.error(`Error fetching article with id ${id}:`, error));
    };

    // Fetch articles on mount
    React.useEffect(() => {
        fetchArticles();
    }, []);

    const data = {
        labels: ["January", "February", "March", "April", "May", "June", "July"],
        datasets: [
            {
                label: "Dataset",
                data: [65, 59, 80, 81, 56, 55, 40],
                fill: false,
                borderColor: "rgb(75, 192, 192)",
                tension: 0.1,
            },
        ],
    };

    return (
        <div className="App">
            <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                <div style={{ width: '50%' }}>
                    <Line data={data} />
                </div>
                <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                    {articles.map((article) => (
                        <button
                            key={article.id}
                            style={{ margin: '0 10px' }}
                            onClick={() => fetchArticle(article.id)}
                        >
                            {article.title}
                        </button>
                    ))}
                </div>
                {currentArticle && (
                    <div style={{ marginTop: '20px', padding: '10px', background: 'white', borderRadius: '5px' }}>
                        <h2>{currentArticle.title}</h2>
                        <p>{currentArticle.content}</p>
                    </div>
                )}
            </header>
        </div>
    );
}

export default App;
