import React, { useEffect, useState } from "react";
import "./App.css";
import GraphPage from "./GraphPage";
import ArticleSelection from "./ArticleSelection";
import { BrowserRouter as Router } from 'react-router-dom';
import BackgroundParticles from "./BackgroundParticles"; // Import Router

function App() {
    const [page, setPage] = useState(1);
    const [selectedArticles, setSelectedArticles] = useState([]);

    useEffect(() => {
        if (page === 1) {
            setSelectedArticles([]);
        }
    }, [page]);

    return (
        <Router>  {/* Wrap your App with Router */}
            <div className="App">
                <div class="gradient-section">
                    <div className="container">
                        {page === 1 && (
                            <>
                                <h1>Financial Data Visualization</h1>
                                <h2>Select Articles to Plot</h2>
                                <div className="card">
                                    <ArticleSelection setSelectedArticles={setSelectedArticles} />
                                    <button onClick={() => setPage(2)}>Plot</button>
                                </div>
                            </>
                        )}
                        {page === 2 && <GraphPage selectedArticles={selectedArticles} setPage={setPage} />}
                    </div>
                </div>
            </div>
        </Router>
    );
}

export default App;
