import React, { useState } from "react";

function ArticleSelection() {
  const [articleUrl, setArticleUrl] = useState("");
  const [articleData, setArticleData] = useState(null);
  const [error, setError] = useState("");

  const handleUrlChange = (event) => {
    setArticleUrl(event.target.value);
  };

  const handleFetchArticle = () => {
    // Make sure to handle empty URL input
    if (!articleUrl.trim()) {
      setError("Please enter an article URL.");
      return;
    }

    setError("");
    setArticleData(null);

    console.log(articleUrl);

    fetch(
      `http://localhost:3001/api/articles/by-url?url=${encodeURIComponent(
        articleUrl,
      )}`,
    )
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else if (response.status === 404) {
          throw new Error("Article not found");
        } else {
          throw new Error("An error occurred while fetching the article");
        }
      })
      .then((data) => {
        setArticleData(data);
      })
      .catch((err) => {
        setError(err.message);
      });
  };

  return (
    <div>
      <input
        type="text"
        value={articleUrl}
        onChange={handleUrlChange}
        placeholder="Enter article URL"
      />
      <button onClick={handleFetchArticle}>Fetch Article</button>

      {error && <div className="error">{error}</div>}

      {articleData && (
        <div className="article">
          <h3>{articleData.title}</h3>
          <p>Source: {articleData.source}</p>
          <p>Subjectivity Score: {articleData.subjectivity_score}</p>
          <p>Sentiment Score: {articleData.sentiment_score}</p>
          <p>Category: {articleData.category}</p>
        </div>
      )}
    </div>
  );
}

export default ArticleSelection;
