// GraphPage.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Scatter } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    Title,
    Tooltip,
    Legend
);

function GraphPage({ selectedArticles, setPage }) {
    const [articlePoints, setArticlePoints] = useState([]);

    const articleColors = ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'];

    useEffect(() => {
        console.log(selectedArticles);
        if (selectedArticles.length) {
            fetchSelectedArticles();
        }
    }, [selectedArticles]);

    const fetchSelectedArticles = async () => {
        const requests = selectedArticles.map(id =>
            fetch(`http://localhost:3001/api/articles/${id}`).then(res => res.json())
        );
        const articlesData = await Promise.all(requests);
        const points = articlesData.map(article => ({
            x: article.sentiment_score,
            y: article.subjectivity_score,
            label: `Title: ${article.title}\nPolarity: ${article.sentiment_score}\nSubjectivity: ${article.subjectivity_score}
            \nCategory: ${article.category}`,
        }));
        setArticlePoints(points);
    };

    const graphData = {
        datasets: [
            {
                label: 'Articles',
                data: articlePoints,
                backgroundColor: articlePoints.map((_, i) => articleColors[i % articleColors.length]),
            },
        ],
    };

    const graphOptions = {
        scales: {
            x: {
                type: 'linear',
                position: 'bottom',
                title: {
                    display: true,
                    text: 'Polarity (Negative vs Positive Sentiment)',
                },
                min: -1,
                max: 1,
                ticks: {
                    stepSize: 0.25,
                },
            },
            y: {
                title: {
                    display: true,
                    text: 'Subjectivity (Opinion vs Fact)',
                },
                min: 0,
                max: 1,
                ticks: {
                    stepSize: 0.2,
                },
            },
        },
        plugins: {
            title: {
                display: false,
                text: 'Article Subjectivity and Polarity',
            },
            legend: {
                display: false,
            },
            tooltip: {
                callbacks: {
                    label: function(context) {
                        var label = context.raw.label;
                        if (label) {
                            // If the label is a single string, split it by the newline character
                            // and return the array of strings to create multiline labels.
                            return label.split('\n');
                        }
                        return label || '';
                    }
                }
            }
        },
        responsive: true,
        maintainAspectRatio: false,
    };

    return (
        <div className="card">
            <div>
                <h1>Article Subjectivity and Polarity</h1>
                <div style={{ height: '500px', width: '100%' }}>
                    <Scatter data={graphData} options={graphOptions} />
                </div>
                <button onClick={() => setPage(1)}>Back to Article Selection</button>
            </div>
        </div>
    );
}

export default GraphPage;
