// ArticleSelection.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./ArticleSelection.css";
import "font-awesome/css/font-awesome.min.css";

function ArticleSelection({ setSelectedArticles }) {
  const [articles, setArticles] = useState([]);
  const navigate = useNavigate();
  const [url, setUrl] = useState("");

  useEffect(() => {}, []);

  const fetchArticles = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/articles"); // Absolute URL
      const data = await response.json();
      console.log(data);
      setArticles(data.articles);
    } catch (error) {
      console.error("Failed fetching articles:", error);
    }
  };
  const handleCheckboxChange = (title) => {
    setSelectedArticles((prev) => {
      if (prev.includes(title)) {
        return prev.filter((articleId) => articleId !== title);
      } else {
        return [...prev, title];
      }
    });
  };

  const handleUrlSubmit = (event) => {
    event.preventDefault();
    // Implement what you want to do with the URL here
    // For example, sending it to a server or logging it to the console
    console.log(url);
  };

  const handleInputChange = (event) => {
    setUrl(event.target.value);
  };

  function getIconForCategory(category) {
    switch (category) {
      case "Technology":
        return "fa-laptop";
      case "Politics":
        return "fa-university";
      case "Sports":
        return "fa-soccer-ball-o";
      default:
        return "fa-newspaper-o";
    }
  }

  const handlePlot = () => {
    navigate("/graph");
  };

  return (
    <div className="article-selection-page">
      <h1>
        Select
        <br />
        Articles
      </h1>
      <form>
        <label htmlFor="url">Enter URL:</label>
        <br />
        <input
          type="url"
          id="url"
          name="url"
          // value={url}
          // onChange={handleInputChange}
          placeholder="https://example.com"
          pattern="https?://.*"
          required
        />
        <button type="submit">Submit</button>
      </form>
      <div className="articles-container">
        {articles.map((article) => (
          <div className={`article-item ${article.category}`} key={article.id}>
            <input
              type="checkbox"
              id={`checkbox-${article.title}`}
              title={article.title}
              value={article.title}
              onChange={() => handleCheckboxChange(article.title)}
              className="hidden-checkbox"
            />
            <label
              htmlFor={`checkbox-${article.title}`}
              className="styled-checkbox"
            ></label>
            <label
              className="article-label"
              htmlFor={`checkbox-${article.title}`}
            >
              <div className="article-title">{article.title}</div>
            </label>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ArticleSelection;
