// App.js

import React from "react";
import logo from "./logo.svg";
import "./App.css";

function App() {
    const [articles, setArticles] = React.useState([]);
    const [currentArticle, setCurrentArticle] = React.useState(null);

    const fetchArticles = () => {
        fetch("/api/articles")
            .then((res) => res.json())
            .then((data) => setArticles(data.articles))
            .catch((error) => console.error('Error fetching articles:', error));
    };

    const fetchArticle = (id) => {
        fetch(`/api/articles/${id}`)
            .then((res) => res.json())
            .then((data) => setCurrentArticle(data))
            .catch((error) => console.error(`Error fetching article with id ${id}:`, error));
    };

    return (
        <div className="App">
            <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                <button onClick={fetchArticles}>Load Articles</button>
                <div>
                    {!articles.length ? (
                        <p>No articles to display</p>
                    ) : (
                        <ul>
                            {articles.map((article) => (
                                <li key={article.id} onClick={() => fetchArticle(article.id)}>
                                    {article.title}
                                </li>
                            ))}
                        </ul>
                    )}
                    {currentArticle && (
                        <div>
                            <h1>{currentArticle.title}</h1>
                            <p>{currentArticle.content}</p>
                        </div>
                    )}
                </div>
            </header>
        </div>
    );
}

export default App;
