// ArticleSelection.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, CardActionArea, CardContent, Typography, Checkbox, IconButton } from '@mui/material';
import { styled } from '@mui/material/styles';

import './ArticleSelection.css';
import 'font-awesome/css/font-awesome.min.css';

import TechnologyIcon from '@mui/icons-material/LaptopMac';
import PoliticsIcon from '@mui/icons-material/AccountBalance';
import SportsIcon from '@mui/icons-material/SportsSoccer';
import DefaultIcon from '@mui/icons-material/Article';

function ArticleSelection({ selectedArticles, setSelectedArticles }) {
    const [articles, setArticles] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchArticles();
    }, []);

    const fetchArticles = async () => {
        try {
            const response = await fetch('http://localhost:3001/api/articles'); // Absolute URL
            const data = await response.json();
            console.log(data);
            setArticles(data.articles);
        } catch (error) {
            console.error("Failed fetching articles:", error);
        }
    };
    const handleCheckboxChange = (title) => {
        setSelectedArticles(prev => {
            if (prev.includes(title)) {
                return prev.filter(articleId => articleId !== title);
            } else {
                return [...prev, title];
            }
        });
    };

    function getIconForCategory(category) {
        switch (category) {
            case 'Technology': return <TechnologyIcon />;
            case 'Politics': return <PoliticsIcon />;
            case 'Sports': return <SportsIcon />;
            default: return <DefaultIcon />;
        }
    }

    // Category specific style
    const categoryStyles = {
        Technology: '#E3F2FD',
        Politics: '#E8F5E9',
        Sports: '#FFF9C4',
        default: '#fff'
    };

    const StyledCard = styled(Card)(({ theme, category }) => ({
        marginBottom: theme.spacing(2),
        padding: theme.spacing(1),
        display: 'flex',
        alignItems: 'center',
        backgroundColor: categoryStyles[category] || categoryStyles.default,
        '&:hover': {
            boxShadow: theme.shadows[4],
        },
    }));

    const IconWrapper = styled('div')({
        marginRight: '16px',
    });

    const handlePlot = () => {
        navigate('/graph');
    };

    return (
        <div>
            <h1>Select Articles</h1>
            <div className="articles-container">
                {articles.map((article) => (
                    <StyledCard category={article.category} key={article.id}>
                        <Checkbox
                            checked={selectedArticles.includes(article.title)}
                            onChange={() => handleCheckboxChange(article.title)}
                            inputProps={{ 'aria-label': 'Select article' }}
                        />
                        <CardContent>
                            <Typography variant="subtitle1" component="h2">
                                {getIconForCategory(article.category)} {article.title}
                            </Typography>
                        </CardContent>
                    </StyledCard>
                ))}
            </div>
        </div>
    );
}

export default ArticleSelection;
