// ArticleSelection.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ArticleSelection.css';
import 'font-awesome/css/font-awesome.min.css';

function ArticleSelection({ setSelectedArticles }) {
    const [articles, setArticles] = useState([]);
    const navigate = useNavigate();
    const [currentStep, setCurrentStep] = useState(1); // Assuming this is step 1
    const totalSteps = 3; // Assuming there are 3 steps in total


    useEffect(() => {
        fetchArticles();
    }, []);

    const fetchArticles = async () => {
        try {
            const response = await fetch('http://localhost:3001/api/articles'); // Absolute URL
            const data = await response.json();
            console.log(data);
            setArticles(data.articles);
        } catch (error) {
            console.error("Failed fetching articles:", error);
        }
    };
    const handleCheckboxChange = (title) => {
        setSelectedArticles(prev => {
            if (prev.includes(title)) {
                return prev.filter(articleId => articleId !== title);
            } else {
                return [...prev, title];
            }
        });
    };

    function getIconForCategory(category) {
        switch (category) {
            case 'Technology': return 'fa-laptop';
            case 'Politics': return 'fa-university';
            case 'Sports': return 'fa-soccer-ball-o';
            default: return 'fa-newspaper-o';
        }
    }

    const handlePlot = () => {
        navigate('/graph');
    };

    return (
        <div>
            <h1>Select<br />Articles</h1>
            <div className="articles-container">
            {articles.map((article) => (
                <div className={`article-item ${article.category}`} key={article.id}>
                    <input
                        type="checkbox"
                        id={`checkbox-${article.title}`}
                        title={article.title}
                        value={article.title}
                        onChange={() => handleCheckboxChange(article.title)}
                        className="hidden-checkbox"
                    />
                    <label htmlFor={`checkbox-${article.title}`} className="styled-checkbox"></label>
                    <label className="article-label" htmlFor={`checkbox-${article.title}`}>
                        <div className="article-title">{article.title}</div>
                    </label>
                </div>
            ))}
            </div>
        </div>
    );
}

export default ArticleSelection;
