const particlesOptions = {
    fullScreen: {
        enable: true,
        zIndex: -1 // Use a negative z-index to position the particles behind your content
    },
    particles: {
        number: {
            value: 80,
            density: {
                enable: true,
                value_area: 800
            }
        },
        color: {
            value: "#0000FF" // Particle color (white)
        },
        shape: {
            type: "circle", // Particle shape
        },
        opacity: {
            value: 0.5,
            random: false,
            anim: {
                enable: false,
            }
        },
        size: {
            value: 3,
            random: true, // Random particle sizes
            anim: {
                enable: false,
            }
        },
        line_linked: {
            enable: true,
            distance: 150,
            color: "#ffffff", // Line color
            opacity: 0.4,
            width: 1
        },
        move: {
            enable: true,
            speed: 2, // The speed of particle movement
            direction: "none", // Move particles in all directions
            random: false,
            straight: false, // Particles move in straight lines
            out_mode: "out", // Particles move out of the canvas
            attract: {
                enable: false,
            }
        }
    },
    interactivity: {
        detect_on: "canvas",
        events: {
            onhover: {
                enable: true,
                mode: "repulse" // The particles will move away from the mouse
            },
            onclick: {
                enable: true,
                mode: "push" // More particles will be added on click
            },
            resize: true
        },
        modes: {
            grab: {
                distance: 400,
                line_linked: {
                    opacity: 1
                }
            },
            bubble: {
                distance: 400,
                size: 40,
                duration: 2,
                opacity: 8,
                speed: 3
            },
            repulse: {
                distance: 200
            },
            push: {
                particles_nb: 4
            },
            remove: {
                particles_nb: 2
            }
        }
    },
    retina_detect: true // Adjust the number of particles for retina displays
};

export default particlesOptions;
