// ArticleSelection.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function ArticleSelection({ setSelectedArticles }) {
    const [articles, setArticles] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchArticles();
    }, []);

    const fetchArticles = async () => {
        try {
            const response = await fetch('http://localhost:3001/api/articles'); // Absolute URL
            const data = await response.json();
            console.log(data);
            setArticles(data.articles);
        } catch (error) {
            console.error("Failed fetching articles:", error);
        }
    };
    const handleCheckboxChange = (title) => {
        setSelectedArticles(prev => {
            if (prev.includes(title)) {
                return prev.filter(articleId => articleId !== title);
            } else {
                return [...prev, title];
            }
        });
    };

    const handlePlot = () => {
        navigate('/graph');
    };

    return (
        <div>
            <h1>Select Articles</h1>
            {articles.map((article) => (
                <div className="article-item" key={article.id}>
                    <input
                        type="checkbox"
                        id={`checkbox-${article.id}`}
                        title={article.title}
                        value={article.id}
                        onChange={() => handleCheckboxChange(article.title)}
                    />
                    <label className="article-label" htmlFor={`checkbox-${article.id}`}>{article.title}</label>
                </div>
            ))}
        </div>
    );
}

export default ArticleSelection;
