// App.js
import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { Line } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

function App() {
    const [articles, setArticles] = React.useState([]);

    // Fetch articles
    const fetchArticles = () => {
        fetch("/api/articles")
            .then((res) => res.json())
            .then((data) => setArticles(data.articles))
            .catch((error) => console.error('Error fetching articles:', error));
    };

    // Fetch articles on mount
    React.useEffect(() => {
        fetchArticles();
    }, []);

    const data = {
        labels: ["January", "February", "March", "April", "May", "June", "July"],
        datasets: [
            {
                label: "Dataset",
                data: [65, 59, 80, 81, 56, 55, 40],
                fill: false,
                borderColor: "rgb(75, 192, 192)",
                tension: 0.1,
            },
        ],
    };

    return (
        <div className="App">
            <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                <div style={{ width: '50%' }}>
                    <Line data={data} />
                </div>
                <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                    {articles.map(article => (
                        <button key={article.id} style={{ margin: '0 10px' }}>
                            {article.title}
                        </button>
                    ))}
                </div>
            </header>
        </div>
    );
}

export default App;
