import React, { useState, useEffect } from "react";

// Assuming that axios is installed and imported to make HTTP requests.
import axios from "axios";

function ArticleSelection() {
  const [articleUrl, setArticleUrl] = useState("");
  const [articleData, setArticleData] = useState(null);
  const [articleNotFound, setArticleNotFound] = useState(false);

  useEffect(() => {
    // If there is no article URL, don't attempt a lookup.
    if (!articleUrl) return;

    // Function to fetch article data by URL.
    const fetchArticleData = async () => {
      try {
        const response = await axios.get(
          `/api/articlesByUrl?url=${encodeURIComponent(articleUrl)}`,
        );
        setArticleData(response.data);
        setArticleNotFound(false); // Reset this in case it was previously set.
      } catch (error) {
        if (error.response && error.response.status === 404) {
          // If the article was not found, update state accordingly.
          setArticleNotFound(true);
        }
        // Handle other potential errors here, if necessary.
      }
    };

    fetchArticleData();
  }, [articleUrl]); // This effect runs when articleUrl changes.

  // Event handler for when the user submits the form.
  const handleFormSubmit = (event) => {
    event.preventDefault();
    // Here you could validate the URL or do other checks before setting it.
    setArticleUrl(event.target.elements.articleUrl.value);
  };

  return (
    <div>
      <form onSubmit={handleFormSubmit}>
        <input type="text" name="articleUrl" placeholder="Enter article URL" />
        <button type="submit">Check Article</button>
      </form>

      {articleNotFound && <div>Article not found.</div>}
      {articleData && (
        <div>
          <h1>{articleData.title}</h1>
          {/* ...other article details */}
        </div>
      )}
    </div>
  );
}

export default ArticleSelection;
