// App.js
import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { Line, Scatter } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

import { useState, useEffect } from "react";

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

function App() {
    const [articles, setArticles] = useState([]);
    const [currentArticle, setCurrentArticle] = useState(null);

    const [graphData, setGraphData] = React.useState({
        datasets: [{
            label: 'Articles',
            data: [],
            backgroundColor: 'rgba(255, 99, 132, 1)'
        }]
    });

    // Fetch articles
    const fetchArticles = () => {
        fetch("/api/articles")
            .then((res) => res.json())
            .then((data) => setArticles(data.articles))
            .catch((error) => console.error('Error fetching articles:', error));
    };

    // Function to fetch a single article
    const fetchArticle = (id) => {
        setCurrentArticle(null); // Clear the current article
        fetch(`/api/articles/${id}`)
            .then((res) => res.json())
            .then((data) => setCurrentArticle(data))
            .catch((error) => console.error(`Error fetching article with id ${id}:`, error));
    };

    // Fetch articles on mount
    React.useEffect(() => {
        fetchArticles();
    }, []);

    // Handler for when an article button is clicked
    const handleArticleClick = (article) => {
        console.log(article);
        const newPoint = {
            x: article.polarity, // Assuming article has a polarity property
            y: article.subjectivity, // Assuming article has a subjectivity property
            label: article.title // This will show the article title in the tooltip
        };

        // Update the graph data with the new point
        setGraphData({
            datasets: [{
                ...graphData.datasets[0],
                data: [...graphData.datasets[0].data, newPoint]
            }]
        });
    };

    // Scatter plot data and options
    const scatterData = {
        datasets: [],
    };

    const scatterOptions = {
        scales: {
            x: {
                type: 'linear',
                position: 'bottom',
                title: {
                    display: true,
                    text: 'Polarity (Negative vs Positive Sentiment)',
                },
                min: -1,
                max: 1,
                ticks: {
                    stepSize: 0.25,
                },
            },
            y: {
                title: {
                    display: true,
                    text: 'Subjectivity (Opinion vs Fact)',
                },
                min: 0,
                max: 1,
                ticks: {
                    stepSize: 0.2,
                },
            },
        },
        plugins: {
            title: {
                display: true,
                text: 'Article Subjectivity and Polarity',
            },
            legend: {
                display: false,
            },
            tooltip: {
                callbacks: {
                    label: function(context) {
                        return context.dataset.label || '';
                    }
                }
            }
        },
        responsive: true,
        maintainAspectRatio: false,
    };

    return (
        <div className="App">
            <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />

                <div className="graph-container">
                    <Scatter data={scatterData} options={scatterOptions} />
                </div>

                <div className="article-buttons">
                    {articles.map((article) => (
                        <button
                            key={article.id}
                            className="article-button"
                            onClick={() => handleArticleClick(article)}
                        >
                            {article.title}
                        </button>
                    ))}
                </div>

                {currentArticle && (
                    <div className="article-info">
                        <h2>{currentArticle.title}</h2>
                        <p>{currentArticle.content}</p>
                    </div>
                )}
            </header>
        </div>
    );
}

export default App;
