import React, { useState } from "react";
import "./App.css";
import GraphPage from "./GraphPage";
import ArticleSelection from "./ArticleSelection";  // Import the ArticleSelection component

function App() {
    const [page, setPage] = useState(1);
    const [selectedArticles, setSelectedArticles] = useState([]);  // State to store selected articles

    return (
        <div className="App">
            <div className="container">
                <h1>Financial Data Visualization</h1>
                <h2>Select Articles to Plot</h2>
                {page === 1 && (
                    <div className="card">
                        <ArticleSelection setSelectedArticles={setSelectedArticles} />
                        <button onClick={() => setPage(2)}>Plot</button>
                    </div>
                )}
                {page === 2 && <GraphPage selectedArticles={selectedArticles} />}  // Pass selectedArticles as a prop
            </div>
        </div>
    );
}

export default App;
