// App.js
import React from "react";
import "./App.css";
import { Line, Scatter } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

import { useState, useEffect } from "react";

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

function App() {
    const [articles, setArticles] = useState([]);
    const [currentArticle, setCurrentArticle] = useState(null);
    const [articlePoints, setArticlePoints] = useState({});

    const articleColors = ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'];

    // Fetch articles
    const fetchArticles = async () => {
        try {
            let response = await fetch("/api/articles");
            let data = await response.json();
            setArticles(data.articles);

            let points = {};
            data.articles.forEach(article => {
                points[article.id] = {
                    x: article.polarity,
                    y: article.subjectivity,
                    label: article.title,
                    backgroundColor: article.color,
                    show: false
                };
            });

            setArticlePoints(points);
        } catch (error) {
            console.error('Error fetching articles:', error);
        }
    };

    // Function to fetch a single article
    const fetchArticle = (id) => {
        setCurrentArticle(null); // Clear the current article
        fetch(`/api/articles/${id}`)
            .then((res) => res.json())
            .then((data) => setCurrentArticle(data))
            .catch((error) => console.error(`Error fetching article with id ${id}:`, error));
    };

    // Fetch articles on mount
    React.useEffect(() => {
        fetchArticles();
    }, []);

    // Handler for when an article button is clicked
    const handleArticleClick = (articleId) => {
        setArticlePoints((prevArticlePoints) => ({
            ...prevArticlePoints,
            [articleId]: {
                ...prevArticlePoints[articleId],
                show: !prevArticlePoints[articleId].show,
            },
        }));
    };

    const graphData = {
        datasets: [
            {
                label: 'Articles',
                data: Object.values(articlePoints).filter((point) => point.show).map(point => ({
                    ...point,
                    backgroundColor: point.backgroundColor, // Set color for each point individually
                })),
                // Disable animations
                animation: {
                    duration: 0, // or you could use a specific duration if you prefer
                },
            },
        ],
    };

    // Scatter plot data and options
    const scatterData = {
        datasets: [],
    };

    const scatterOptions = {
        scales: {
            x: {
                type: 'linear',
                position: 'bottom',
                title: {
                    display: true,
                    text: 'Polarity (Negative vs Positive Sentiment)',
                    font: {
                        size : '20',
                        font: "Arial, Helvetica, sans-serif"
                    }
                },
                min: -1,
                max: 1,
                ticks: {
                    stepSize: 0.25,
                },
            },
            y: {
                title: {
                    display: true,
                    text: 'Subjectivity (Opinion vs Fact)',
                    font: {
                        size : '20'
                    }
                },
                min: 0,
                max: 1,
                ticks: {
                    stepSize: 0.2,
                },
            },
        },
        plugins: {
            title: {
                display: true,
                text: 'Article Subjectivity and Polarity',
                font: {
                    size : '25'
                }
            },
            legend: {
                display: false,
            },
            tooltip: {
                callbacks: {
                    label: function(context) {
                        return context.dataset.label || '';
                    }
                }
            }
        },
        responsive: true,
        maintainAspectRatio: false,
    };

    return (
        <div className="App">
            <header className="App-header">
                {/* Header content goes here, if any */}
            </header>
            <main className="App-content">
                <div className="graph-container">
                    <Scatter data={graphData} options={scatterOptions} />
                </div>

                <div className="article-buttons">
                    {articles.map((article) => (
                        <button
                            key={article.id}
                            className="article-button"
                            onClick={() => handleArticleClick(article.id)}
                            style={{ backgroundColor: article.color }}
                        >
                            {article.title}
                        </button>
                    ))}
                </div>

                {currentArticle && (
                    <div className="article-info">
                        <h2>{currentArticle.title}</h2>
                        <p>{currentArticle.content}</p>
                    </div>
                )}
            </main>
        </div>
    );
}

export default App;
