import React, { useState } from "react";
import { BrowserRouter } from 'react-router-dom';
import "./App.css";
import GraphPage from "./GraphPage";

function App() {
    const [page, setPage] = useState(1);

    return (
        <BrowserRouter>
            <div className="App">
                <div className="container">
                    <h1>Sentiment Analysis</h1>
                    <h2>Select Articles to Plot</h2>
                    {page === 1 && (
                        <div className="card">
                            {/* Your articles with checkboxes will be here */}
                            <button onClick={() => setPage(2)}>Plot</button>
                        </div>
                    )}
                    {page === 2 && <GraphPage />}
                </div>
            </div>
        </BrowserRouter>
    );
}

export default App;
