import React, { useState } from "react";
import { GraphPage } from "./GraphPage";
import "./App.css";

function App() {
    const [page, setPage] = useState(1);

    return (
        <div className="App">
            <div className="container">
                <h1>Financial Data Visualization</h1>
                <h2>Select Articles to Plot</h2>
                {page === 1 && (
                    <div className="card">
                        {/* Your articles with checkboxes will be here */}
                        <button onClick={() => setPage(2)}>Plot</button>
                    </div>
                )}
                {page === 2 && <GraphPage />}
            </div>
        </div>
    );
}

export default App;
