// ArticleSelection.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, CardActionArea, CardContent, Typography, Checkbox, IconButton } from '@mui/material';
import { styled } from '@mui/material/styles';

import './ArticleSelection.css';
import 'font-awesome/css/font-awesome.min.css';

function ArticleSelection({ setSelectedArticles }) {
    const [articles, setArticles] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchArticles();
    }, []);

    const fetchArticles = async () => {
        try {
            const response = await fetch('http://localhost:3001/api/articles'); // Absolute URL
            const data = await response.json();
            console.log(data);
            setArticles(data.articles);
        } catch (error) {
            console.error("Failed fetching articles:", error);
        }
    };
    const handleCheckboxChange = (title) => {
        setSelectedArticles(prev => {
            if (prev.includes(title)) {
                return prev.filter(articleId => articleId !== title);
            } else {
                return [...prev, title];
            }
        });
    };

    function getIconForCategory(category) {
        switch (category) {
            case 'Technology': return 'fa-laptop';
            case 'Politics': return 'fa-university';
            case 'Sports': return 'fa-soccer-ball-o';
            default: return 'fa-newspaper-o';
        }
    }

    const StyledCard = styled(Card)(({ theme }) => ({
        marginBottom: theme.spacing(2),
        padding: theme.spacing(1),
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-start',
    }));

    const IconWrapper = styled('div')({
        marginRight: '16px',
    });

    const handlePlot = () => {
        navigate('/graph');
    };

    return (
        <div>
            <h1>Select Articles</h1>
            <div className="articles-container">
                {articles.map((article) => (
                    <StyledCard variant="outlined" key={article.id}>
                        <CardActionArea onClick={() => handleCheckboxChange(article.title)}>
                            <IconWrapper>
                                <IconButton>
                                    <i className={`fa ${getIconForCategory(article.category)}`} aria-hidden="true"></i>
                                </IconButton>
                            </IconWrapper>
                            <CardContent>
                                <Typography variant="h6" component="div">
                                    {article.title}
                                </Typography>
                            </CardContent>
                            <Checkbox
                                edge="end"
                                onChange={() => handleCheckboxChange(article.title)}
                                checked={selectedArticles.includes(article.title)}
                                inputProps={{ 'aria-labelledby': `checkbox-list-label-${article.title}` }}
                            />
                        </CardActionArea>
                    </StyledCard>
                ))}
            </div>
        </div>
    );
}

export default ArticleSelection;
